
var swiper = new Swiper('.swiper-container', {
    // slidesPerView: 7,
    spaceBetween: 15,
    rightSlides: true,
    pagination: {
        el: '.swiper-pagination',
        clickable: true,
    },
    breakpoints: {
        768: {
            slidesPerView: 5
        },
        992: {
            slidesPerView: 7
        },
        1200: {
            slidesPerView: 8
        }
    }
});


      window.onscroll = () => {
  const nav = document.querySelector('#navbar');
  if(this.scrollY <= 10) nav.className = ''; else nav.className = 'scroll';
};
